var passport = require('passport');
var util = require('util');

var OAE = require('oae-util/lib/oae');
var LoginId = require('oae-authentication/lib/model').LoginId;

var AuthenticationAPI = require('oae-authentication');
var AuthenticationConstants = require('oae-authentication/lib/constants').AuthenticationConstants;
var AuthenticationUtil = require('oae-authentication/lib/util');

/**
 * Handle a request that performs local authentication
 *
 * @param  {Request}    req     The express request object
 * @param  {Response}   res     The express response object
 * @api private
 */
var _handleLocalAuthentication = function(req, res, next) {
    var strategyId = AuthenticationUtil.getStrategyId(req.tenant, AuthenticationConstants.providers.LOCAL);
    var errorHandler = AuthenticationUtil.handlePassportError(req, res, next);
    passport.authenticate(strategyId)(req, res, errorHandler);
};

/**
 * Handle a callback request where a user has successfully authenticated to the system from an
 * external system
 *
 * @param  {Request}    req                     The express request object
 * @param  {User}       req.oaeAuthInfo.user    The authenticated user
 * @param  {Response}   res                     The express response object
 * @api private
 */
var _handleLocalAuthenticationSuccess = function(req, res, next) {
    // Simply return a 200 response with the user object
    res.send(200, req.oaeAuthInfo.user);
};

/**
 * Handle a request to determine if a username exists
 *
 * @param  {Request}    req                         The express request object
 * @param  {Context}    req.ctx                     The context of the current request
 * @param  {String}     req.params.username         The username to check for existence
 * @param  {String}     [req.params.tenantAlias]    The alias of the tenant in which to check for the username. If unspecified, defaults to the current tenant in context
 * @param  {Response}   res                         The express response object
 * @api private
 */
var _handleLocalUsernameExists = function(req, res) {
    AuthenticationAPI.localUsernameExists(req.ctx, req.params.tenantAlias, req.params.username, function(err, exists) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        // If the login id doesn't exist, we send back a 404
        if (exists) {
            return res.send(200);
        } else {
            return res.send(404);
        }
    });
};

/**
 * Handle a request to change a user's password
 *
 * @param  {Request}    req                     The express request object
 * @param  {Context}    req.ctx                 The context of the current request
 * @param  {String}     req.params.userId       The id of the user whose password to change
 * @param  {String}     req.body.newPassword    The password to change the current password to
 * @param  {String}     [req.body.oldPassword]  The previous password, only needed if the current user is not an administrator
 * @param  {Response}   res                     The express response object
 * @api private
 */
var _handleChangePassword = function(req, res) {
    AuthenticationAPI.changePassword(req.ctx, req.params.id, req.body.oldPassword, req.body.newPassword, function(err, changed) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200);
    });
};

/**
 * Handle a request to create a private tenant administrator user
 *
 * @param  {Request}    req                         The express Request object
 * @param  {String}     req.body.displayName        The display name of the user
 * @param  {String}     req.body.username           The username the user will use to login
 * @param  {String}     req.body.password           The password the user will use to login
 * @param  {String}     [req.body.email]            The email address of the user
 * @param  {String}     [req.body.locale]           The user's locale
 * @param  {String}     [req.body.timezone]         The user's timezone
 * @param  {String}     [req.body.publicAlias]      The public alias of the user
 * @param  {String}     [req.body.acceptedTC]       `'true'` if the user has accepted the terms and conditions. Any other value indicates they have not
 * @param  {String}     [req.params.tenantAlias]    The tenant in which to create the tenant admin user. Defaults to the tenant of the current request
 * @param  {Response}   res                         The express Response object
 * @api private
 */
var _handleCreateTenantAdminUser = function(req, res) {
    var ctx = req.ctx;
    var tenantAlias = req.params.tenantAlias || ctx.tenant().alias;
    var loginId = new LoginId(tenantAlias, AuthenticationConstants.providers.LOCAL, req.body.username, {'password': req.body.password});

    // In addition to the global admin parameters, tenant admins still accept the terms and conditions
    var opts = _getGlobalAdminOptParameters(ctx, req.body);
    opts.acceptedTC = (req.body.acceptedTC === 'true');

    // Create the user as a tenant admin
    AuthenticationAPI.createTenantAdminUser(ctx, loginId, req.body.displayName, opts, function(err, user) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(201, user);
    });
};

/*!
 * Create a global admin user with mapped local authentication credentials
 */
OAE.globalAdminRouter.on('post', '/api/auth/createGlobalAdminUser', function(req, res) {
    var opts = _getGlobalAdminOptParameters(req.ctx, req.body);

    // Create the user as global admin
    AuthenticationAPI.getOrCreateGlobalAdminUser(req.ctx, req.body.username, req.body.password, req.body.displayName, opts, function(err, user, created) {
        if (err) {
            return res.send(err.code, err.msg);
        } else if (!created) {
            return res.send(400, util.format('A user with username "%s" already exists', req.body.username));
        }

        return res.send(201, user);
    });
});

/*!
 * Create a tenant admin user with the mapped local authentication credentials
 */
OAE.globalAdminRouter.on('post', '/api/auth/:tenantAlias/createTenantAdminUser', _handleCreateTenantAdminUser);
OAE.tenantRouter.on('post', '/api/auth/createTenantAdminUser', _handleCreateTenantAdminUser);

/*!
 * Login to a tenant
 */
OAE.globalAdminRouter.on('post', '/api/auth/login', [_handleLocalAuthentication, _handleLocalAuthenticationSuccess]);
OAE.tenantRouter.on('post', '/api/auth/login', [_handleLocalAuthentication, _handleLocalAuthenticationSuccess]);

/*!
 * Change a user's password
 */
OAE.globalAdminRouter.on('post', '/api/user/:id/password', _handleChangePassword);
OAE.tenantRouter.on('post', '/api/user/:id/password', _handleChangePassword);

/*!
 * Check whether or not a username exists
 */
OAE.globalAdminRouter.on('get', '/api/auth/:tenantAlias/exists/:username', _handleLocalUsernameExists);
OAE.tenantRouter.on('get', '/api/auth/exists/:username', _handleLocalUsernameExists);

/**
 * Extract the optional admin user profile parameters from the given set of request parameters
 *
 * @param  {Object}     parameters  The parameters from which to extract the profile information
 * @return {Object}                 The relevant profile parameters for admin users
 * @api private
 */
var _getGlobalAdminOptParameters = function(ctx, parameters) {
    return {
        'email': parameters.email,
        'locale': parameters.locale || ctx.locale(),
        'timezone': parameters.timezone,
        'publicAlias': parameters.publicAlias
    };
};
