var passport = require('passport');
var util = require('util');

var Context = require('oae-context').Context;
var PrincipalsDAO = require('oae-principals/lib/internal/dao');
var Signature = require('oae-util/lib/signature');
var Validator = require('oae-util/lib/validator').Validator;
var AuthenticationUtil = require('oae-authentication/lib/util');

var Strategy = function() {
    passport.Strategy.call(this);
    this.name = 'signed';
};

/**
 * Inherit from `passport.Strategy`
 */
util.inherits(Strategy, passport.Strategy);


/**
 * Authenticate request based on the contents of request parameters
 *
 * @param  {Request}   req  The Express Request object that is trying to authenticate
 * @api protected
 */
Strategy.prototype.authenticate = function(req) {
    var expires = req.body.expires;
    var signature = req.body.signature;
    var userId = req.body.userId;
    var tenantAlias = req.ctx.tenant().alias;

    var self = this;
    var validator = new Validator();
    validator.check(expires, {'code': 400, 'msg': 'Missing expires parameter'}).notEmpty();
    validator.check(signature, {'code': 400, 'msg': 'Missing signature parameter'}).notEmpty();
    validator.check(userId, {'code': 400, 'msg': 'Missing userId parameter'}).notEmpty();
    if (validator.hasErrors()) {
        var err = validator.getFirstError();
        return self.fail(err.msg, err.code);
    } else if (!Signature.verifyExpiringSignature({'tenantAlias': tenantAlias, 'userId': userId}, expires, signature)) {
        return self.fail('Invalid signature credentials', 401);
    }

    // This is a valid request, get the user and pass it on
    PrincipalsDAO.getPrincipal(userId, function(err, user) {
        if (err && err.code !== 404) {
            return self.error(new Error(err.msg));
        } else if (err && err.code === 404) {
            return self.fail(err.msg, 404);
        }

        AuthenticationUtil.logAuthenticationSuccess(req, user, self.name);
        return self.success(user);
    });
};

/**
 * Expose `Strategy`
 */
module.exports = Strategy;
