var _ = require('underscore');

var Invitation = module.exports.Invitation = function(resourceId, email, inviterUserId, role) {
    var that = {};
    that.resourceId = resourceId;
    that.email = email;
    that.inviterUserId = inviterUserId;
    that.role = role;

    /**
     * Get a raw storage representation of an Invitation
     *
     * @return {Object}     An object that can be persisted to storage for this invitation
     */
    that.toStorage = function() {
        return _.pick(that, 'resourceId', 'email', 'inviterUserId', 'role');
    };
};

/**
 * Convert the given storage object into an invitation object
 *
 * @param  {Object}     hash    The raw storage representation of the invitation
 * @return {Invitation}         A proper invitation object
 */
Invitation.fromStorage = function(hash) {
    return new Invitation(hash.resourceId, hash.email, hash.token, hash.inviterUserId, hash.role);
};
