var OAE = require('oae-util/lib/oae');


var AcropolisIngester = require('./ingester');

OAE.tenantRouter.on('get', '/api/acropolis/ingest', function(req, res) {
    AcropolisIngester.process(req.ctx, function(err, items) {
        if (err) {
            return res.send(500, err.msg);
        }

        res.send(200, items);
    })
});
