var OAE = require('oae-util/lib/oae');

var ConfigAPI = require('oae-config');


//////////////////
// Global admin //
//////////////////

/*!
 * Global config schema
 */
OAE.globalAdminServer.get('/api/config/schema', function(req, res) {
    res.send(200, ConfigAPI.getSchema());
});

/*!
 * Global admin config values
 */
OAE.globalAdminServer.get('/api/config', function(req, res) {
    res.send(200, ConfigAPI.getTenantConfig(req.ctx.tenant().alias));
});

/*!
 * Tenant config values
 */
OAE.globalAdminServer.get('/api/config/:tenantAlias', function(req, res) {
    res.send(200, ConfigAPI.getTenantConfig(req.params.tenantAlias));
});

/*!
 * Update global admin config value
 */
OAE.globalAdminServer.post('/api/config', function(req, res) {
    ConfigAPI.updateConfig(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/*!
 * Update tenant config value
 */
OAE.globalAdminServer.post('/api/config/:tenantAlias', function(req, res) {
    ConfigAPI.updateConfig(req.ctx, req.params.tenantAlias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

/////////////
// Tenants //
/////////////

/*!
 * Global config schema
 */
OAE.tenantServer.get('/api/config/schema', function(req, res) {
    res.send(200, ConfigAPI.getSchema());
});

/*!
 * Tenant config values
 */
OAE.tenantServer.get('/api/config', function(req, res) {
    res.send(200, ConfigAPI.getConfig(req.ctx.tenant().alias));
});

/*!
 * Update tenant config value
 */
OAE.tenantServer.post('/api/config', function(req, res) {
    ConfigAPI.updateConfig(req.ctx, req.ctx.tenant().alias, req.body, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
});

