var _canManage = function(currentUser, contentId, callback) {
    PermissionsAPI.isAllowed(currentUser.id, ContentConstants.roles.MANAGER, contentId, function(err, canManage) {
        if (err) {
            return callback(err);
        } else if (!canManage) {
            return callback({'code': 401, 'msg': 'You are not a manager of this piece of content'});
        }
        callback(null, canManage);
    });
};

