var getLibraryItems = module.exports.getLibraryItems = function(currentUser, principalId, callback) {
    var validator = new Validator();
    validator.check(principalId, {'code': 400, 'msg': 'A content id must be provided'}).notEmpty();
    if (validator.hasErrors()) {
        return callback(validator.getErrors());
    }

    // TODO: Check if I have access to the principalId

    if (!currentUser) {
        principalId = principalId + ':public';
    } else if (currentUser.id !== principalId) {
        principalId = principalId + ':loggedin';
    }

    Cassandra.runQuery("SELECT * FROM LibraryByPrinciple WHERE principalId = ?", [principalId], function (err, rows) {
        if (err) {
            return callback({'code': 500, 'msg': err});
        }

        var items = [];
        for (var r = 0; r < rows[0].cols.length; r++) {
            if (rows[0].cols[r].name !== "principalId") {
                items.push(rows[0].cols[r].value);
            }
        }
        // TODO: Add the actual profile info of the content
        callback(null, items);
    });
};

