var Tenant = require('oae-tenants/lib/model').Tenant;
var User = require('oae-principals/lib/model.user').User;
var PrincipalAPI = require('oae-principals');

var ContentAPI = require('oae-content');
var Content = require('oae-content/lib/model').Content;

// Data used inside of the tests
var tenant1 = new Tenant('cam', 'Cambridge University', 'Cambridge University description', 2001);
var tenant2 = new Tenant('gt', 'Georgia Tech', 'Georgia Tech description', 2002);

/**
 * TODO
 */
var setUpUsers = function(callback) {
    var r = Math.floor(Math.random() * 10000);
    var users = [new User(tenant1, 'u:cam:nm417-' + r, 'nm417-' + r, 'public', 'Nicolaas', 'Matthijs', 'Nicolaas Matthijs'),
                 new User(tenant1, 'u:cam:simong-' + r, 'simong-' + r, 'loggedin', 'Simon', 'Gaeremynck', 'Simon Gaeremynck'),
                 new User(tenant1, 'u:cam:bp323-' + r, 'bp323-' + r, 'private', 'Bert', 'Pareyn', 'Bert Pareyn'),
                 new User(tenant2, 'u:gt:branden-' + r, 'branden-' + r, 'public', 'Branden', 'Visser', 'Branden Visser')];
    var done = 0;
    var createUserDone = function(err) {
        if (err) {
            throw err;
        }
        done++;
        if (done === users.length) {
            callback(users);
        }
    };
    // Create users
    for (var u = 0; u < users.length; u++) {
        PrincipalAPI.createUser(users[u].tenant, users[u].username, 'password' + u, users[u].privacy, users[u].firstName, users[u].lastName, users[u].displayName, createUserDone);
    }
};

/**
 * TODO
 */
var checkPieceOfContent = function(test, currentUser, creator, contentObj, expectAccess, expectInLibrary, callback) {
    // Check whether the content can be retrieved
    ContentAPI.getContent(currentUser, contentObj.contentId, function(err, retrievedContent) {
        if (expectAccess) {
            test.ok(!err);
            test.ok(retrievedContent.contentId);
            test.equal(retrievedContent.tenant, contentObj.tenant);
            test.equal(retrievedContent.visibility, contentObj.visibility);
            test.equal(retrievedContent.title, contentObj.title);
            test.equal(retrievedContent.description, contentObj.description);
            test.equal(retrievedContent.contentType, contentObj.contentType);
            test.ok(retrievedContent.lastModified);
        } else {
            test.ok(err);
            test.ok(!retrievedContent);
        }
        // TODO: Check hasAccess
        // TODO: Check canManage
        // Check if the item comes back in the library
        ContentAPI.getLibraryItems(currentUser, creator.id, function(err, contentItems) {
            test.ok(!err);
            if (expectInLibrary) {
                test.equal(contentItems.length, 1);
                test.equal(contentItems[0], contentObj.contentId);
            } else {
                test.equal(contentItems.length, 0);
            }
            callback();
        });
    });
};

/**
 * TODO
 * @param {Object} test //TODO
 */
module.exports.createContent = function(test) {
    test.expect(16);

    setUpUsers(function(users) {
        // Create one as anon user
        ContentAPI.createContent(users[0].tenant.alias, null, 'public', 'Test Content 1', 'Test content description 1', 'link', function(err, contentObj) {
            test.ok(err);
            test.ok(!contentObj);
            // Create one with all required fields
            ContentAPI.createContent(users[0].tenant.alias, users[0], 'public', 'Test Content 2', 'Test content description 2', 'link', function(err, contentObj) {
                test.ok(!err);
                test.ok(contentObj.contentId);
                // Create one without description
                ContentAPI.createContent(users[0].tenant.alias, users[0], 'public', 'Test Content 3', null, 'link', function(err, contentObj) {
                    test.ok(!err);
                    test.ok(contentObj.contentId);
                    // Create one without type
                    ContentAPI.createContent(users[0].tenant.alias, users[0], 'public', 'Test Content 4', 'Test content description 4', null, function(err, contentObj) {
                        test.ok(err);
                        test.ok(!contentObj);
                        // Create one without title
                        ContentAPI.createContent(users[0].tenant.alias, users[0], 'public', null, 'Test content description 5', 'link', function(err, contentObj) {
                            test.ok(err);
                            test.ok(!contentObj);
                            // Create one without permission
                            ContentAPI.createContent(users[0].tenant.alias, users[0], null, 'Test Content 6', 'Test content description 6', 'link', function(err, contentObj) {
                                test.ok(!err);
                                test.ok(contentObj.contentId);
                                // Check if the permission has been set to public (default)
                                ContentAPI.getContent(users[0], contentObj.contentId, function(err, contentObj) {
                                    test.ok(!err);
                                    test.equal(contentObj.visibility, 'public');
                                    // Create one without tenant
                                    ContentAPI.createContent(null, users[0], 'public', 'Test Content 7', 'Test content description 7', 'link', function(err, contentObj) {
                                        test.ok(err);
                                        test.ok(!contentObj);
                                        test.done();
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });
    });
};

/**
 * TODO
 * @param {Object} test //TODO
 */
module.exports.createPublicContentItem = function(test) {
    test.expect(35);

    setUpUsers(function(users) {
        // Create a public content item
        var contentObj = new Content(users[0].tenant.alias, null, 'public', 'Test Content 1', 'Test content description 1', 'link');
        ContentAPI.createContent(contentObj.tenant, users[0], contentObj.visibility, contentObj.title, contentObj.description, contentObj.contentType, function(err, content) {
            test.ok(!err);
            test.ok(content.contentId);
            contentObj.contentId = content.contentId;
            // TODO: Share with manager user
            // TODO: Share with viewer user
            // Get the piece of content as the person who created the content
            checkPieceOfContent(test, users[0], users[0], contentObj, true, true, function() {
                // TODO: Check for manager user
                // TODO: Check for viewer user
                checkPieceOfContent(test, users[1], users[0], contentObj, true, true, function() {
                    checkPieceOfContent(test, null, users[0], contentObj, true, true, function() {
                        test.done();
                    });
                });
            });
        });
    });
};

