var _ = require('underscore');

var Cassandra = require('oae-util/lib/cassandra');

module.exports = function(config, callback) {
    return ensureSchema(callback);
};

/**
 * Create the data model in Cassandra for the invitations functionality
 *
 * @param  {Function}         callback       Standard callback function
 * @param  {Object}           callback.err   An error that occurred, if any
 * @api private
 */
var ensureSchema = function(callback) {
    Cassandra.createColumnFamilies({
        'Invitations': 'CREATE TABLE "AuthzInvitations" ("resourceId" text, "email" text, "token" text, "inviterUserId" text, "role" text, PRIMARY KEY ("resourceId", "email"))',
        'InvitationsTokens': 'CREATE TABLE "AuthzInvitationTokens" ("token" text PRIMARY KEY, "resourceId" text, "email" text)'
    }, callback);
};
