var assert = require('assert');

var ConfigTestUtil = require('oae-config/lib/test/util');

/**
 * Enable the mediacore configuration for a particular tenant
 *
 * @param  {RestContext}    tenantAdminRestContext  The rest context of a tenant admin who should configure the tenant
 * @param  {String}         url                     The URL of the mediacore server to configure
 * @param  {String}         key                     The key id for the mediacore requests
 * @param  {String}         secret                  The secret string to use to sign the requests
 * @param  {Number}         colletionId             The mediacore collection id to use to store videos
 * @param  {Function}       callback                Invoked when the tenant has been configured for mediacore
 */
var enableMediaCore = module.exports.enableMediaCore = function(tenantAdminRestContext, url, key, secret, collectionId, callback) {
    // Enable the mediacore preview processor and configuring it to talk to our new web server
    var update = {
        'oae-mediacore/mediacore/enabled': true,
        'oae-mediacore/mediacore/url': url,
        'oae-mediacore/mediacore/keyId': key,
        'oae-mediacore/mediacore/secret': secret,
        'oae-mediacore/mediacore/collectionId': collectionId
    };

    ConfigTestUtil.updateConfigAndWait(tenantAdminRestContext, null, update, function(err) {
        assert.ok(!err);
        return callback();
    });
};
