var _ = require('underscore');
var Mixpanel = require('mixpanel');


var client = null;

/**
 * Initialize the mixpanel
 *
 * @param  {Object}     config              The mixpanel configuration object
 * @param  {Boolean}    config.enabled      Whether or not mixpanel event tracking should be enabled
 * @param  {String}     config.token        The mixpanel API token
 */
var init = module.exports.init = function(config) {
    if (config.enabled) {
        // Initialize a client
        client = Mixpanel.init(config.token);

        // Register all the listeners
        _registerListeners(client);
    }
};

/**
 * Register the event listeners that will track each event with mixpanel
 *
 * @api private
 */
var _registerListeners = function() {
    require('./eventlisteners/authentication')(client);
    require('./eventlisteners/content')(client);
    require('./eventlisteners/discussions')(client);
    require('./eventlisteners/folders')(client);
};
