var _ = require('underscore');

var PrincipalsAPI = require('oae-principals');
var PrincipalsConstants = require('oae-principals/lib/constants').PrincipalsConstants;

var MixpanelUtil = require('oae-mixpanel/lib/util');

module.exports = function(client) {

    /*!
     * Retrieving a group
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.RETRIEVED_GROUP_PROFILE, function(ctx, group) {
        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = group.id;
        params.visibility = group.visibility;
        client.track(PrincipalsConstants.events.RETRIEVED_GROUP_PROFILE, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.RETRIEVED_GROUP_PROFILE);
    });

    /*!
     * Creating a group
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.CREATED_GROUP, function(ctx, group, members) {
        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = group.id;
        params.visibility = group.visibility;
        params.nrOfMembers = _.values(members).length;
        client.track(PrincipalsConstants.events.CREATED_GROUP, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.CREATED_GROUP);
    });

    /*!
     * Updating a group
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.UPDATED_GROUP, function(ctx, newGroup, oldGroup) {
        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = newGroup.id;
        params.newVisibility = newGroup.visibility;
        params.oldVisibility = oldGroup.oldVisibility;
        client.track(PrincipalsConstants.events.UPDATED_GROUP, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.UPDATED_GROUP);
    });

    /*!
     * Sharing a group / Updating its members
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.UPDATED_GROUP_MEMBERS, function(ctx, group, roleChanges, newMemberIds, updatedMemberIds, removedMemberIds) {
        newMemberIds = newMemberIds || [];
        updatedMemberIds = updatedMemberIds || [];
        removedMemberIds = removedMemberIds || [];

        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = group.id;
        params.visibility = group.visibility;
        params.newMembers = newMemberIds.length;
        params.updatedMembers = updatedMemberIds.length;
        params.removedMembers = removedMemberIds.length;
        params.deltaMembers = params.newMembers - params.removedMembers;
        client.track(PrincipalsConstants.events.UPDATED_GROUP_MEMBERS, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.UPDATED_GROUP_MEMBERS);
    });

    /*!
     * Joining a group
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.JOINED_GROUP, function(ctx, group) {
        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = group.id;
        params.visibility = group.visibility;
        client.track(PrincipalsConstants.events.JOINED_GROUP, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.JOINED_GROUP);
    });

    /*!
     * Leaving a group
     */
    MixpanelUtil.listen(PrincipalsAPI, PrincipalsConstants.events.LEFT_GROUP, function(ctx, group) {
        var params = MixpanelUtil.getBasicParameters(ctx);
        params.id = group.id;
        params.visibility = group.visibility;
        client.track(PrincipalsConstants.events.LEFT_GROUP, params);
        client.people.increment(params.distinct_id, PrincipalsConstants.events.LEFT_GROUP);
    });
};
