var _ = require('underscore');
var util = require('util');

// Manage all handlers that have been registered for performing operations when a principal has been
// deleted in the system
var _handlers = {};

var registerDeleteHandler = module.exports.registerDeleteHandler = function(name, handler) {
    if (_handlers[name]) {
        throw new Error(util.format('Attempted to register multiple error handlers for name "%s"', name));
    } else if (!_.isFunction(handler)) {
        throw new Error(util.format('Attempted to register non-function error handler for name "%s"', name));
    }

    _handlers[name] = handler;
};
