var _ = require('underscore');

// since principals is a collection of multiple sub-APIs, to propagate events up to the root API, we need to
// catch their events and emit from here. We can't have the sub-APIs push events directly onto this module because
// it it forms a cross dependency
var emitter = module.exports = require('./internal/emitter');

var GroupAPI = require('./api.group');
var UserAPI = require('./api.user');

// This file would become unmaintainable if all the logic would be placed here.
// That's why we split them up in a couple of files of which the api logic gets exported.
_.extend(module.exports, GroupAPI, UserAPI);
