var _ = require('underscore');
var util = require('util');

var AuthzUtil = require('oae-authz/lib/util');
var OaeUtil = require('oae-util/lib/util');
var ResourceActions = require('oae-resource/lib/actions');
var ResourceConstants = require('oae-resource/lib/constants').ResourceConstants;

var PrincipalsConstants = require('oae-principals/lib/constants').PrincipalsConstants;
var PrincipalsDAO = require('oae-principals/lib/internal/dao');
var PrincipalsEmitter = require('oae-principals/lib/internal/emitter');
var PrincipalsUtil = require('oae-principals/lib/util');

var log = require('oae-logger').logger('oae-principals-invitations')

ResourceActions.when(ResourceConstants.events.ACCEPTED_INVITATION, function(ctx, invitationHashes, memberChangeInfosByResourceId, callback) {
    var memberChangeInfosByGroupId = {};
    _.chain(memberChangeInfosByResourceId)
        .keys()
        .filter(AuthzUtil.isGroupId)
        .each(function(groupId) {
            memberChangeInfosByGroupId[groupId] = memberChangeInfosByResourceId[groupId];
        })
        .value();
    if (_.isEmpty(memberChangeInfosByGroupId)) {
        return callback();
    }

    var groupIds = _.keys(memberChangeInfosByGroupId);
    PrincipalsDAO.getPrincipals(groupIds, null, function(err, groupsById) {
        if (err) {
            log().warn({
                'err': err,
                'groupIds': groupIds
            }, 'An error occurred while getting groups to update group libraries after invitation was accepted');
            return callback();
        }

        // Invoke the "accept invitation" handler with the resources when we have them
        callback(null, _.values(groupsById));

        // Update the last modified timestamp of each group that we are added to and invoke the
        // joined group event on the principals emitter, which will handle updating all additional
        // updates (e.g., library indexes, search, activity)
        _.each(groupsById, function(group) {
            var memberChangeInfo = memberChangeInfosByGroupId[group.id];
            if (_.isEmpty(memberChangeInfo.changes)) {
                // Don't do anything for this group if no authz role changes were actually made
                return;
            }

            PrincipalsUtil.touchLastModified(group, function(err, updatedGroup) {
                if (err) {
                    return log().warn({
                        'err': err,
                        'groupId': group.id
                    }, 'An error occurred while updating group libraries after invitation was accepted');
                }

                return PrincipalsEmitter.emit(PrincipalsConstants.events.JOINED_GROUP, ctx, updatedGroup, group, memberChangeInfo);
            });
        });
    });
});
