///////////
// USERS //
///////////


module.exports.PRIVACY_SETTING = {'private':true, 'loggedin': true, 'public': true};

module.exports.User = function(tenant, id, username, privacy, firstName, lastName, displayName) {
    var that = {};

    that.tenant = tenant;
    that.id = id;
    that.username = username;
    that.privacy = privacy;
    that.firstName = firstName;
    that.lastName = lastName;
    that.displayName = displayName || that.firstName + " " + that.lastName;

    return that;
};

module.exports.rowToUser = function(row) {
    return module.exports.User(row.colHash.tenant, row.colHash.principal_id, row.colHash.user_username, row.colHash.privacy_setting, row.colHash.user_first_name, row.colHash.user_last_name, row.colHash.user_display_name);
};




////////////
// GROUPS //
////////////

module.exports.Group = function(tenant, principal_id, privacy, name, description) {
    var that = {};

    that.principal_id = principal_id;
    that.tenant = tenant;
    that.privacy = privacy;
    that.name = name;
    that.description = description;

    return that;
};


module.exports.rowToGroup = function (row) {
    return module.exports.Group(row.colHash.tenant, row.colHash.principal_id, row.colHash.privacy_setting, row.colHash.group_title, row.colHash.group_description);
};