var OAE = require('oae-util/lib/oae');

var PrincipalsAPI = require('./api');

/*!
 * Returns the terms and conditions for the current tenant
 */
OAE.tenantServer.get('/api/termsAndConditions', function(req, res) {
    var locale = null;

    // Determine the locale for which we should retrieve the T&C
    // 1. A query string parameter has highest priority
    if (req.query.locale) {
        locale = req.query.locale;

    // 2. If the user is logged in, we check if he has set the locale value
    } else if (req.ctx.user() && req.ctx.user().locale) {
        locale = req.ctx.user().locale;

    // 3. Check the Accept-Language header that was sent by the browser
    } else if (req.headers['accept-language']) {
        // Convert `en-us,en,dk` to `en_US`
        locale = req.headers['accept-language'].split(',')[0].replace(/-([^-]*$)/, function(match, capture) {
            return '_' + capture.toUpperCase();
        });

    // 4. Otherwise we'll need to default to whatever the tenant admin used
    } else {
        locale = 'default';
    }

    var termsAndConditions = PrincipalsAPI.getTermsAndConditions(req.ctx, locale);
    res.send(200, termsAndConditions);
});
