var RoleUtils = require('oae-roles/lib/util');

var PrincipalUtil = require('oae-principals/lib/util');

exports.testIdentifiers = function(test) {
    var id = RoleUtils.toUuid("g", "cam", "oae-team");
    test.ok(PrincipalUtil.isGroup(id));

    id = RoleUtils.toUuid("u", "cam", "simong");
    test.ok(PrincipalUtil.isUser(id));


    id = RoleUtils.toUuid("c", "cam", "foo.doc");
    test.ok(!PrincipalUtil.isUser(id));
    test.ok(!PrincipalUtil.isUser(id));
    test.done();
};