var _ = require('underscore');

var OAE = require('oae-util/lib/oae');
var OaeUtil = require('oae-util/lib/util');

var ResourceActions = require('oae-resource/lib/actions');

var log = require('oae-logger').logger('oae-resource-rest');

OAE.tenantRouter.on('post', '/api/invitations/accept', function(req, res) {
    ResourceActions.acceptInvitation(req.ctx, req.body.token, function(err, email, resources) {
        if (err) {
            return res.send(err.code, err.msg);
        }

        return res.send(200, {'email': email, 'resources': resources});
    });
});
