/**
 * @RESTModel Tenant
 *
 * @Required    [alias,displayName,host]
 * @Property    {string}        alias                   Unique short name for tenant in URLs (should be lowercase)
 * @Property    {string}        displayName             Display name for tenant
 * @Property    {string}        host                    Host address for tenant
 * @Property    {boolean}       active                  Is tenant active?
 * @Property    {boolean}       deleted                 Is tenant deleted?
 * @Property    {boolean}       isGlobalAdminServer     Is tenant the global administrative server?
 */

/**
 * @RESTModel TenantInfo
 *
 * @Required    [alias,displayName]
 * @Property    {string}        alias                   Short name for tenant
 * @Property    {string}        displayName             Displayable name for tenant
 */

/**
 * @RESTModel TenantNetwork
 *
 * @Required  [displayName, id]
 * @Property  {string}              displayName         Display name for tenant network
 * @Property  {string}              id                  Short name for tenant network
 * @Property  {Tenant[]}            tenants             Tenants associated with tenant network
 */

/**
 * @RESTModel TenantNetworks
 *
 * @Required  []
 * @Property  {TenantNetwork}       {tenantAlias}       Tenant network for named tenant
 */

/**
 * @RESTModel Tenants
 *
 * The Tenants object may contain information about multiple tenants, each entry
 * is keyed by its tenant alias, for example:
 *
 *     "test": {
 *         "alias": "test",
 *         "displayName": "CasperJS Tenant",
 *         "host": "test.oae.com",
 *         "active": true,
 *         "deleted": false,
 *         "isGlobalAdminServer": false
 *     }
 *
 * @Required  []
 * @Property  {Tenant[]}            {tenantAlias}       Detailed information for named tenant
 */
