/**
 * A Statement model
 *
 * @param  {Actor}      actor		Object model containing actor information
 * @param  {Verb}       verb		Object model containing verb information
 * @param  {Object}     object		Object model containing object information
 * @return {Statement}  that		Object model containing the body for the request to TinCan
 */
module.exports.Statement = function(actor, verb, object) {
	var that = {};
	that.actor = actor;
	that.verb = verb;
	that.object = object;
	return that;
};

/**
 * An Actor model
 *
 * @param  {String}  name			The user's displayname
 * @param  {String}  homePage		The link to the user's profile
 * @return {Actor}   that			Object model containing information about the actor
 */
module.exports.Actor = function(name, homePage) {
	var that = {};
	that.name = name;
	that.objectType = 'Agent';
	that.account = {};
	that.account.homePage = homePage;
	that.account.name = name;
	return that;
};

/**
 * An Object model
 *
 * @param  {String}  id				The activity id
 * @param  {String}  id				The activity id
 * @return {Object}  that			Object model containing information about the object
 */
module.exports.Object = function(id, name, description) {
	var that = {};
	that.id = id;
	that.objectType = 'Activity';
	that.definition = {};
	that.definition.name = {};
	that.definition.name['en-US'] = name;
	that.definition.description = {};
	that.definition.description['en-US'] = description;
	return that;
};

/**
 * A Verb model
 *
 * @param  {String}  id				The TinCan identifier of the verb
 * @param  {String}  display		The display name of the verb
 * @return {Verb}    that			Object model containing information about the verb
 */
module.exports.Verb = function(id, display) {
	var that = {};
	that.id = id;
	that.display = {};
	that.display['en-US'] = display;
	return that;
};
