var OAE = require('oae-util/lib/oae');

var VersionAPI = require('./api');

/**
 * @REST getVersion
 *
 * Get the version of OAE
 *
 * @Server      admin,tenant
 * @Method      GET
 * @Path        /version
 * @Return      {string}                          The currently running version
 * @HttpResponse                    200           The version will be returned
 */
OAE.tenantRouter.on('get', '/api/version', getVersion);
OAE.globalAdminRouter.on('get', '/api/version', getVersion);

function getVersion(req, res) {
    VersionAPI.getVersion(function(version) {
        return res.status(200).send(version);
    });
}
